<?phpnamespace Controller;use Model\ActiveRecord;use Model\Cita;use Model\Servicio;class APIController {    public static function index(): void    {        //obtiene los servicios desde la bd y los convierte en json para consumirlo en el front        $servicios = Servicio::all();        echo json_encode($servicios);    }        public static function guardarCita(): void    {        $cita = new Cita($_POST);        $resultado = $cita->guardar();        echo json_encode($resultado);    }}