<?phpnamespace Controller;use Clases\Request;use MVC\models\AdminCita;use MVC\Router;class AdminController{    public static function index(Router $router): void    {        $session = new Request();                $session->startSession();                //consultar la bd        $admin = new AdminCita();        $citas = $admin->getAllCitas();                $userName = $session->session('nombre');                $router->render('admin/index',             [                "crrntUser" => $userName,                "citas" => $citas            ]);    }}