<?phpnamespace Controller;use Clases\Request;use MVC\Router;class CitaController{    public static function index(Router $router): void    {        $request = new Request();        $request->startSession();                //verifica si el usuario esta autenticado        isUserAuth();                $userName = $request->session('nombre');        $idUsuario = $request->session('id');                $router->render('cita/index',        [            "crrntUser" => $userName,            "idUsuario" => $idUsuario,        ]);    }}